import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import DiscussionPage from "./DiscussionPage";
import ProfilePage from "./ProfilePage";
import SignUpPage from "./SignUpPage";
import MainContent from "./MainContent";

// Navbar component
function Navbar() {
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <Link className="navbar-brand" to="/challenges">Coding Challenges</Link>
                <div className="dropdown">
                    <button className="btn btn-secondary dropdown-toggle" type="button" id="fontDropdownMenuButton"
                            data-bs-toggle="dropdown" aria-expanded="false">
                        Select Font
                    </button>
                    <ul className="dropdown-menu" aria-labelledby="fontDropdownMenuButton">
                        <li>
                            <button className="dropdown-item" onClick={() => handleFontChange('Arial')}>Arial</button>
                        </li>
                        <li>
                            <button className="dropdown-item" onClick={() => handleFontChange('Helvetica')}>Helvetica
                            </button>
                        </li>
                        <li>
                            <button className="dropdown-item" onClick={() => handleFontChange('Verdana')}>Verdana
                            </button>
                        </li>
                        {/* Add more font options as needed */}
                    </ul>
                </div>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
                        aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item">
                            <Link className="nav-link" to="/challenges">Challenges</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/discussions">Discussions</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/profile">Profile</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="btn btn-primary" to="/signup">Sign Up / Log In</Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
}


// App component
function App() {
    // State variable for background color
    const [bgColor, setBgColor] = useState('#ffffff'); // Default to white

    // Handler function to update background color
    const handleBgColorChange = (color) => {
        setBgColor(color);
    };

    // State variable for font family
    const [fontFamily, setFontFamily] = useState('Arial'); // Default font

    // Handler function to update font family
    const handleFontChange = (font) => {
        setFontFamily(font);
    };

    return (
        <Router>
            <div style={{fontFamily: fontFamily}}>
                <Navbar handleFontChange={handleFontChange}/>
                <div style={{backgroundColor: bgColor}}>
                    <Navbar/>
                    {/* Color selection control */}
                    <div style={{position: 'fixed', bottom: '20px', right: '20px', zIndex: '9999'}}>
                        <input type="color" value={bgColor} onChange={(e) => handleBgColorChange(e.target.value)}/>
                    </div>
                    {/* Routes */}
                    <Routes>
                        <Route path="/challenges" element={<MainContent/>}/>
                        <Route path="/discussions" element={<DiscussionPage/>}/>
                        <Route path="/profile" element={<ProfilePage/>}/>
                        <Route path="/signup" element={<SignUpPage/>}/>
                    </Routes>
                </div>
            </div>
        </Router>
);
}

export default App;
