import React from "react";
import {Avatar} from "@mui/material";
import "./Posts.css"

const Posts = ({author, image, preview_text}) => {
    return (
        <div className="posts">
            <div className="posts__top">
                <Avatar src={image}/>
                <h6 className="posts__author">{author}</h6>
            </div>
            <img className="posts__image" src={image} alt="Image post"/>
            <h5>{preview_text}</h5>
        </div>
    );
};

export default Posts;
